import styles from './Write.module.css'
import NewLife from '../../../image/NewLife.jpeg'

import { useState } from 'react'

const Write = ({ updateList }) => {
  const [title, setTitle] = useState('')

  const submitHandler = (event) => {
    event.preventDefault()

    updateList(title)
    setTitle('')
  }

  const TitleHandler = (event) => {
    setTitle(event.target.value)
  }
  return (
    <div className={styles.write}>
      <img src={NewLife} alt='new life' className={styles.image}></img>
      <form className={styles.writeForm} onSubmit={submitHandler}>
        <div className={styles.writeFormGroup}>
          <label htmlFor='fileInput' className={styles.writeInput}>
            <div className={styles.writeIcon}>
              <i class='fa-solid fa-plus'></i>
            </div>
          </label>
          <input
            type='file'
            id='fileInput'
            className={styles.writeInput}
            style={{ display: 'none' }}
          ></input>
          <input
            type='text'
            placeholder='Tell me your stories....'
            value={title}
            onChange={TitleHandler}
            autoFocus={true}
            className={styles.writeInput}
            style={{ fontSize: '30px', width: '100vh' }}
          />
        </div>
        <div className={styles.writeFormGroup}></div>
        <button className={styles.submit}>Publish</button>
      </form>
    </div>
  )
}

export default Write
