import { useState } from 'react'
import Story from './Story'
import Write from './Write'
const Update = () => {
  const [story, setStory] = useState([
    { id: 1, title: '' },
    { id: 2, title: '' },
  ])

  const updateList = (story) => {
    setStory((prevStory) => {
      return [story, ...prevStory]
    })
  }

  const deleteItemHandler = (id) => {
    const deleteItems = story.filter((el) => el.id !== id)
    setStory(deleteItems)
  }

  return (
    <>
      <Write updateList={updateList}></Write>
      <Story story={story} deleteItemHandler={deleteItemHandler}></Story>
    </>
  )
}

export default Update
