const initialUserState = {
  user: null,
}

export const authentication = (state = initialUserState, action) => {
  switch (action.type) {
    case 'Login':
      return { ...state, user: action.payload }
    case 'Logout':
      return { ...state, user: null }
    default:
      return state
  }
}
