import { useState } from 'react'
import StoryList from './StoryList'
import Write from './Write'
const Update = () => {
  const [story, setStory] = useState([
    {
      title: '',
      id: 's1',
    },
    {
      title: '',
      id: 's2',
    },
  ])

  const updateList = (enter) => {
    setStory((previous) => {
      return [enter, ...previous]
    })
  }

  const removeStoriesHandler = (goalID) => {
    setStory((previous) => {
      const updatedGoals = previous.filter((goal) => goal.id !== goalID)
      return updatedGoals
    })
  }

  return (
    <>
      <Write updateList={updateList}></Write>
      <StoryList
        story={story}
        removeStoriesHandler={removeStoriesHandler}
      ></StoryList>
    </>
  )
}

export default Update
