// import styles from './Write.module.css'
import NewLife from '../../../image/NewLife.jpeg'
import {
  ImageWrapper,
  WriteForm,
  WriteFormGroup,
  WriteWrapper,
  WriteIcon,
  WriteInput,
  Button,
} from './Write.styled'
import { useState } from 'react'

const Write = ({ updateList }) => {
  const [title, setTitle] = useState('')

  const submitHandler = (event) => {
    event.preventDefault()

    updateList(title)
    setTitle('')
  }

  const TitleHandler = (event) => {
    setTitle(event.target.value)
  }
  return (
    <WriteWrapper>
      <ImageWrapper>
        <img src={NewLife} alt='new life'></img>
      </ImageWrapper>
      <WriteForm onSubmit={submitHandler}>
        <WriteFormGroup>
          <WriteInput htmlFor='fileInput'>
            <WriteIcon>
              <i class='fa-solid fa-plus'></i>
            </WriteIcon>
          </WriteInput>
          <WriteInput
            type='file'
            id='fileInput'
            style={{ display: 'none' }}
          ></WriteInput>
          <WriteInput
            type='text'
            placeholder='Tell me your stories....'
            value={title}
            onChange={TitleHandler}
            autoFocus={true}
            style={{ fontSize: '30px', width: '100vh' }}
          ></WriteInput>
        </WriteFormGroup>
        <WriteFormGroup></WriteFormGroup>
        <Button>Publish</Button>
      </WriteForm>
    </WriteWrapper>
  )
}

export default Write
