import { useAuthContext } from './useAuthContext'
import { toast } from 'react-toastify'

export const useLogout = () => {
  const { dispatch } = useAuthContext()
  const logout = () => {
    // remove the user from then local storage
    // therefore no need to communicate with the server

    localStorage.clear()
    // to clear the localStorage

    // dispatch logout action
    const logoutDispatch = dispatch({ type: 'Logout' })

    const successMessage = (message) => {
      toast.success(message, {
        position: 'top-center',
        className: 'custom-toast-container',
        autoClose: 1000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
      })
    }

    if (logoutDispatch) {
      successMessage('You have been logged out successfully')
    }
  }
  return { logout }
  // return logout to use this somewhere in this application
}
