import { useState } from 'react'
import StoryList from './StoryList'
import Write from './Write'
const Update = () => {
  const [story, setStory] = useState([
    {
      id: 1,
      title: '',
    },
  ])

  const updateList = (story) => {
    setStory((prevStory) => {
      return [story, ...prevStory]
    })
  }

  const removeStoriesHandler = (id) => {
    setStory((prevStory) => {
      const removeStories = story.filter((el) => el.id !== id)
      return removeStories
    })
  }

  return (
    <>
      <Write updateList={updateList}></Write>
      <StoryList
        story={story}
        removeStoriesHandler={removeStoriesHandler}
      ></StoryList>
    </>
  )
}

export default Update
