import { useState } from 'react'
import axios from 'axios'
import { useAuthContext } from './useAuthContext'
import { useSelector, useDispatch } from 'react-redux'
import { setToken } from '../components/action'
import { use } from '../../../server/app'

export const useRegister = () => {
  const [error, setError] = useState('')
  const [spinner, setSpinner] = useState(false)
  const { dispatch } = useAuthContext()

  const dispatchTokenState = useSelector((state) => state)
  const dispatchToken = useDispatch()

  const register = async (name, email, password) => {
    setSpinner(true)
    setError('')

    const url = 'http://localhost:3000/user/register'
    const data = {
      name: name,
      email: email,
      password: password,
    }

    try {
      const response = await axios.post(url, data)
      if (response.status === 200) {
        dispatchToken()
        localStorage.setItem('user', response.data, dispatchTokenState)
        dispatch({ type: 'Login', payload: response.data })
        setSpinner(true)
        setError('')
      }
    } catch (error) {
      setSpinner(false)
      setError(error.response.data.error)
      console.log(error.response.data.error)
    }
  }

  return { register, spinner, error, setError }
}
