import login from '../../image/Login.webp'
import { LoginWrapper, Form } from '../login/Login.styled'
import { Link } from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux'
import {
  setName,
  setEmail,
  setPassword,
  setConfirmPassword,
} from '../../components/action'
import { useRegister } from '../../hooks/useRegister'

const Login = ({ registerLink }) => {
  const email = useSelector((state) => state.email)
  const name = useSelector((state) => state.name)
  const password = useSelector((state) => state.password)
  const confirmPassword = useSelector((state) => state.confirmPassword)

  const { register, spinner, error } = useRegister()

  const dispatch = useDispatch()

  const submitRegisterHandler = async (e) => {
    e.preventDefault()

    await register(name, email, password)

    dispatch(setEmail(''))
    dispatch(setName(''))
    dispatch(setPassword(''))
    dispatch(setConfirmPassword(''))
  }
  return (
    <LoginWrapper login={login}>
      <Form onSubmit={submitRegisterHandler}>
        <h1>Register</h1>
        <label>Name</label>
        <input
          type='text'
          name='name'
          onChange={(e) => dispatch(setName(e.target.value))}
          value={name}
        />
        <label>Email</label>
        <input
          type='email'
          name='email'
          onChange={(e) => dispatch(setEmail(e.target.value))}
          value={email}
        />
        <label>Password</label>
        <input
          type='password'
          name='password'
          onChange={(e) => dispatch(setPassword(e.target.value))}
          value={password}
        />
        <label>Confirm Password</label>
        {/* <input
          type='password'
          name='confirmPassword'
          onChange={(e) => dispatch(setConfirmPassword(e.target.value))}
          value={confirmPassword}
        /> */}
        <button type='submit'>Register</button>
        <p>New user?</p>
      </Form>
      <Link to='/login'>
        <button onClick={registerLink}>Login</button>
      </Link>
      {spinner && <p>loading</p>}
      {error && <p>{error}</p>}
    </LoginWrapper>
  )
}

export default Login
